import { useState } from "react";
import axios from "axios";

export default function KMeansPredictor() {
  const [income, setIncome] = useState("");
  const [spendingScore, setSpendingScore] = useState("");
  const [cluster, setCluster] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handlePredict = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post("http://localhost:5000/api/predict", {
        annual_income: parseFloat(income),
        spending_score: parseFloat(spendingScore),
      });
      setCluster(response.data.cluster);
    } catch (error) {
      console.error("Prediction error:", error);
      setError("Failed to connect to the prediction service. Please try again.");
      setCluster(null);
    }
    setLoading(false);
  };

  return (
    <div className="flex flex-col items-center gap-4 p-6 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold">K-Means Customer Segmentation</h1>
      <div className="flex flex-col gap-2 w-64">
        <label className="font-medium">Annual Income:</label>
        <input
          type="number"
          value={income}
          onChange={(e) => setIncome(e.target.value)}
          className="p-2 border rounded"
          placeholder="Enter income"
        />
        <label className="font-medium">Spending Score:</label>
        <input
          type="number"
          value={spendingScore}
          onChange={(e) => setSpendingScore(e.target.value)}
          className="p-2 border rounded"
          placeholder="Enter spending score"
        />
        <button
          onClick={handlePredict}
          className="mt-4 p-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          {loading ? "Predicting..." : "Predict Cluster"}
        </button>
      </div>
      {error && (
        <div className="mt-4 p-2 bg-red-500 text-white rounded">
          <p>{error}</p>
        </div>
      )}
      {cluster !== null && !error && (
        <div className="mt-4 p-4 bg-white rounded shadow">
          <h2 className="text-lg font-semibold">Predicted Cluster: {cluster}</h2>
        </div>
      )}
    </div>
  );
}
