import React, { useState } from "react";
import axios from "axios";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";

export default function KMeansPredictor() {
  const [annualIncome, setAnnualIncome] = useState("");
  const [spendingScore, setSpendingScore] = useState("");
  const [cluster, setCluster] = useState(null);
  const [error, setError] = useState(null);

  const handlePredict = async () => {
    setError(null);
    setCluster(null);
    try {
      const response = await axios.post("http://localhost:5000/predict", {
        annual_income: parseFloat(annualIncome),
        spending_score: parseFloat(spendingScore),
      });
      setCluster(response.data.cluster);
    } catch (err) {
      setError("Error predicting cluster. Please try again.");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-400 to-purple-600">
      <Card className="p-6 bg-white shadow-lg rounded-2xl w-96">
        <CardContent>
          <h2 className="text-xl font-semibold text-center mb-4">K-Means Cluster Predictor</h2>
          <Input 
            type="number" 
            placeholder="Annual Income" 
            value={annualIncome} 
            onChange={(e) => setAnnualIncome(e.target.value)} 
            className="mb-4"
          />
          <Input 
            type="number" 
            placeholder="Spending Score" 
            value={spendingScore} 
            onChange={(e) => setSpendingScore(e.target.value)} 
            className="mb-4"
          />
          <Button onClick={handlePredict} className="w-full">Predict Cluster</Button>
          {cluster !== null && (
            <p className="mt-4 text-center font-bold">Predicted Cluster: {cluster}</p>
          )}
          {error && <p className="mt-4 text-center text-red-500">{error}</p>}
        </CardContent>
      </Card>
    </div>
  );
}
