import React, { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { motion } from "framer-motion";

export default function KMeansPredictor() {
  const [income, setIncome] = useState("");
  const [spending, setSpending] = useState("");
  const [prediction, setPrediction] = useState(null);

  const handlePredict = async () => {
    const response = await fetch("/api/predict", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ income: parseFloat(income), spending: parseFloat(spending) }),
    });
    const data = await response.json();
    setPrediction(data.cluster);
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
      className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-500 to-purple-600"
    >
      <Card className="p-6 w-96 bg-white bg-opacity-90 rounded-2xl shadow-lg">
        <CardContent className="flex flex-col space-y-4">
          <h2 className="text-xl font-bold text-center">Customer Segment Predictor</h2>
          <Input
            type="number"
            placeholder="Annual Income"
            value={income}
            onChange={(e) => setIncome(e.target.value)}
            className="p-2 border rounded-lg"
          />
          <Input
            type="number"
            placeholder="Spending Score"
            value={spending}
            onChange={(e) => setSpending(e.target.value)}
            className="p-2 border rounded-lg"
          />
          <Button onClick={handlePredict} className="bg-blue-600 hover:bg-blue-700 text-white p-2 rounded-lg">
            Predict Cluster
          </Button>
          {prediction !== null && (
            <div className="text-center text-lg font-semibold mt-4">
              Predicted Cluster: <span className="text-blue-600">{prediction}</span>
            </div>
          )}
        </CardContent>
      </Card>
    </motion.div>
  );
}
